mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BOARD_CD = #{boardCode}
AND BOARD_ST = 'N'

mapper.insert:


	SELECT SEQ_BOARD_NO.NEXTVAL FROM DUAL


INSERT INTO BOARD VALUES( 
	#{boardNo}, #{boardTitle}, #{boardContent}, 
	DEFAULT, DEFAULT, DEFAULT, DEFAULT, #{memberNo}, #{boardCode}
)

INSERT INTO BOARD_IMG SELECT SEQ_IMG_NO.NEXTVAL IMG_NO, A.* FROM(
SELECT #{img.imageReName} IMG_RENAME, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageLevel} IMG_LEVEL, #{img.boardNo} BOARD_NO FROM DUAL
) A

mapper.delete:
		DELETE FROM BOARD_IMG 
        WHERE BOARD_NO = ${boardNo} 
        AND IMG_LEVEL IN ( ${deleteList} )

mapper.update:
UPDATE BOARD_IMG SET
IMG_RENAME = #{imageReName},
IMG_ORIGINAL = #{imageOriginal}

WHERE BOARD_NO = #{boardNo}
AND IMG_LEVEL = #{imageLevel}

  		UPDATE BOARD SET
		BOARD_ST = 'Y'
		WHERE BOARD_NO = ${boardNo}

UPDATE BOARD SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}
